sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
    var source = inputs.source;
    var obj = {
        "number": source.getValue("number"),
        "opened_at": source.getValue("opened_at"),
        "impact": source.getValue("impact"),
        "urgency": source.getValue("urgency"),
        "short_description": source.getValue("short_description"),
        "description": source.getValue("description"),
        "category": source.getValue("category"),
        "priority": source.getValue("priority"),
        "sys_id": source.getValue("sys_id"),
	    "subcategory": source.getValue("subcategory"),
	    "state": source.getValue("state")
    }
    outputs.json = JSON.stringify(obj);
})(inputs, outputs);

