sys_variable_value.value:
(function execute(inputs, outputs) {
    var request = inputs.request;
    var item = inputs.items;
    var items_json = [];
    while(item.next()){
        var cat_item = new GlideRecord("sc_cat_item");
        cat_item.get(item.getValue("cat_item"));
        var catalog = new GlideRecord("sc_catalog");
        catalog.get(cat_item.getValue("sc_catalogs"));
        var catalog_obj = {
            "title": catalog.getValue("title"),
            "sys_id": catalog.getValue("sys_id")
        }
        var cat_item_obj = {
            "catalog": catalog_obj,
            "name": cat_item.getValue("name"),
            "sys_id": cat_item.getValue("sys_id")
        }

        var item_obj = {
            "backordered": item.getValue("backordered"),
            "cat_item": cat_item_obj,
            "cmdb_ci": item.getValue("cmdb_ci"),
            "due_date": item.getValue("due_date"),
            "estimated_delivery": item.getValue("estimated_delivery"),
            "number": item.getValue("number"),
            "opened_at": item.getValue("opened_at"),
            "opened_by": item.getValue("opened_by"), 
            "order_guide": item.getValue("order_guide"),
            "quantity": item.getValue("quantity"),
            "request_for": item.getValue("request_for"),
            "stage": item.getValue("stage"),
            "state": item.getValue("state"),
            "sys_id": item.getValue("sys_id")
        }
        items_json.push(item_obj);
    }

    var obj = {
        "approval": request.getValue("approval"),
        "description": request.getValue("description"),
        "due_date": request.getValue("due_date"),
        "location": request.getValue("location"),
        "number": request.getValue("number"),
        "opened_at": request.getValue("opened_at"),
        "opened_by": request.getValue("opened_by"), 
        "price": request.getValue("price"),
        "request_for": request.getValue("request_for"),
        "request_items": items_json,
        "request_state": request.getValue("request_state"),
        "short_description": request.getValue("short_description"),
        "special_instructions": request.getValue("special_instruction"),
        "sys_id": request.getValue("sys_id")
    }
    outputs.json = JSON.stringify(obj);
})(inputs, outputs);

