ecc_agent_script_include.script:
var EdaWebhookForward = Class.create();

EdaWebhookForward.prototype = {

  initialize: function () {
    this.Webhook = Packages.com.redhat.ansible.eda.EdaWebhook;

    this.url = probe.getParameter("url");
    this.token = probe.getParameter("token");
    this.data = probe.getParameter("json_data"); 
  },

  send: function () {
    ms.info("sending " + this.data + " to webhook");
    return this.Webhook.sendRequest(this.url, this.token, this.data);
  },

  type: "EdaWebhookForward"
};
